import { JettonMinter, storeJettonMintMessage } from '@ton-community/assets-sdk';
import { internalOnchainContentToCell } from '@ton-community/assets-sdk/dist/utils';
import { type Address, beginCell, toNano } from '@ton/ton';

const jetton = {
    name: 'Joint Photographic Experts Group',
    description:
        'JPEG is a commonly used method of lossy compression for digital images, particularly for those images produced by digital photography. The degree of compression can be adjusted, allowing a selectable tradeoff between storage size and image quality.',
    image_data:
        'data:image/png;base64,',
    symbol: 'JPEG',
    decimals: 9
};

export function createJettonMaster(admin: Address) {
    return JettonMinter.createFromConfig({
        admin,
        content: internalOnchainContentToCell({
            name: jetton.name,
            description: jetton.description,
            image_data: Buffer.from(jetton.image_data, 'ascii').toString('base64'),
            symbol: jetton.symbol,
            decimals: jetton.decimals
        })
    });
}

export function mintMessage(opts: { from: Address; to: Address; amount?: bigint }) {
    return beginCell()
        .store(
            storeJettonMintMessage({
                queryId: 0n,
                amount: opts.amount ?? 1000000000000000n,
                from: opts.from,
                to: opts.to,
                responseAddress: opts.to,
                forwardPayload: null,
                forwardTonAmount: 1n,
                walletForwardValue: toNano('0.05')
            })
        )
        .endCell();
}
